create table tb_address (id bigint generated by default as identity, city varchar(255), street varchar(255), customer_user_id bigint, primary key (id));
create table tb_customer (email varchar(255), user_id bigint not null, primary key (user_id));
create table tb_item (id bigint generated by default as identity, amount integer, unit_price double, product_id bigint, order_id bigint, primary key (id));
create table tb_order (id bigint generated by default as identity, date timestamp, number bigint, customer_user_id bigint, address_id bigint, seller_user_id bigint, primary key (id));
create table tb_product (id bigint generated by default as identity, name varchar(255), price double, primary key (id));
create table tb_product_suppliers (products_id bigint not null, suppliers_id bigint not null);
create table tb_seller (department varchar(255), user_id bigint not null, primary key (user_id));
create table tb_supplier (id bigint generated by default as identity, name varchar(255), primary key (id));
create table tb_user (id bigint generated by default as identity, name varchar(255), primary key (id));
